insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Armor', '9781433294891', NULL, '{"smallThumbnail": "http://books.google.com/books/content?id=x0_GBwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=x0_GBwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api"}', NULL, '"John Steakley"');
insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Asteroid Mining 101: Wealth for the New Space Economy', '9780990584209', 'The emerging asteroid mining industry has extremely ambitious intentions.
It is within the realm of possibility that their work may usher in a change
in global economics as profound as the Industrial Revolution. As may be
expected, press reports dealing with asteroid mining have been numerous,
ranging in scope from short and breezy to broad and serious, and in quality
from accurate to impressionistic to simply uninformed. There is good reason
to be curious about what may be the biggest game-changer in human economic
history. And there is good reason to look closely at the underlying science
and engineering that form the foundation of this work.', '{"smallThumbnail": "http://books.google.com/books/content?id=Q9a8oAEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=Q9a8oAEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api"}', '2014-01-01'::date, '["John S. Lewis"]');
