insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Spacefaring: The Human Dimension', '9780520236776', 'The author of After Contact: The Human Response to Extraterrestrial Life
looks back on forty years of spaceflight, focusing on the human dimension
of space travel and the psychological issues underpinning the desire to
leave the planet.', '{"smallThumbnail": "http://books.google.com/books/content?id=vaFEIZMqLWgC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=vaFEIZMqLWgC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api"}', '2002-01-01'::date, '["Albert A. Harrison"]');
insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Leviathan Wakes', '9783453529311', 'Im Weltall gibt es kein Gesetz ... Die Menschheit hat unser Sonnensystem
kolonisiert. Auf dem Mond, dem Mars, im Asteroidengürtel und noch darüber
hinaus gibt es Stationen und werden Rohstoffe abgebaut. Doch die Sterne
sind den Menschen bisher verwehrt geblieben. Als der Kapitän eines kleinen
Minenschiffs ein havariertes Schiff aufbringt, ahnt er nicht, welch
gefährliches Geheimnis er in Händen hält ? ein Geheimnis, das die Zukunft
