insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Songs of Distant Earth, The', '9782811202606', 'La Terre se meurt et les derniers représentants de l''espèce humaine
prennent place à bord du Magellan pour un voyage de plusieurs centaines
d''années. Au cours d''une escale sur une planète-océan colonisée longtemps
