insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Mindstar Rising', '9780330537742', 'A reissued edition of the first novel in the Greg Mandel series from the
master of space opera.It''s the 21st century and global warming is here to
stay, so forget the way your country used to look. And get used to the free
market, too – the companies possess all the best hardware, and they''re
calling the shots now. In a world like this, a man open to any offers can
