insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Antares Dawn', '9781929381067', NULL, '{"smallThumbnail": "http://books.google.com/books/content?id=oGzXAAAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=oGzXAAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api"}', '1998-01-01'::date, '["Michael McCollum"]');
insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Coyote', '9781841493695', 'Two decades have passed since the revolution that won Coyote''s independence
from Earth. The colony may be free, but its aging computers, aircraft, and
medical equipment are badly in need of replacement. The colony''s survival
is now in question, and help from Earth is imminent. But there are those
who fear that aid from the mother world may be more hindrance than help. .
. As the second generation of colonists prepare to face the prospect of
another battle with Earth''s forces, a larger question looms: can humanity
settle a new planet without repeating the problems of the world it left
behind? An answer must be found soon. Coyote''s future hangs in the balance.
. .', '{"smallThumbnail": "http://books.google.com/books/content?id=iZdfGwAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=iZdfGwAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api"}', '2006-01-01'::date, '["Allen Steele"]');
