insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Thunder Strike', '9780738800103', 'THE NEW COMET found near Jupiter was an incredible treasure trove of water-
ice and rock. Immediately, the water-starved Luna Republic and sierra
Corporation, a leader in asteroid mining, were squabbling over rights to
the new resource. But all thoughts of profit and fame were abandoned when a
scientific expedition discovered that the comets trajectory placed it on a
collision course with Earth! As scientists struggled to find a way to alter
the comet''s course, world leaders tried desperately to restrain mass panic,
and two lovers quarreled over the direction the comet was to take, all
Earth waited to see if humanity had any future at all', '{"smallThumbnail": "http://books.google.com/books/content?id=J7VWAAAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api", "thumbnail": "http://books.google.com/books/content?id=J7VWAAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api"}', '1998-01-01'::date, '["Michael McCollum"]');
