insert into dependency_book (title, isbn, description, thumbnail, release_date, authors) values ('Project Cyclops', '9781452394657', 'Bantam 1992The Aegean, ex-agent Michael Vance pilots the Odyssey II, a
handmade replica. A Russian gunship with Arab terrorists takes a tiny
island where a U.S. corporation has a laser space facility. The renegades
